import arcade, mathsprites=[]def collide(x1, y1, angle, length, sprite_list, invert=True) -> bool:    """    :param x1: x координата начала луча    :param y1: y координата начала луча    :param angle: угол луча    :param length: длинна луча    :param sprite_list: список спрайтов, с которыми нужно проверять столкновение    :param invert: нужно обнаруживать спрайты или их отсутствие (false = наличие спрайтов)    """    x2 = x1 + length * math.cos(math.radians(angle))    y2 = y1 + length * math.sin(math.radians(angle))    # dx = x2 - x1    # dy = y2 - y1    dx = round(math.cos(angle))    dy = round(math.sin(angle))    i=0    sprites=[]    for i in range(round(length)):        dx = i*math.cos(angle)        dy = i*math.sin(angle)        is_sprite = bool(arcade.get_sprites_at_point((x1+dx, y1+dy), sprite_list))        sprites.append((x1+dx, y1+dy))        #arcade.draw_line_strip(sprites, (255, 255, 255), 5)    if invert:        if False in sprites:            return True    elif not invert:        if True in sprites:            return True    else:        return False